// ---------- Anonymous Feed ----------
function Feed() {
  const [feed, setFeed] = useLocal(KEY.feed, []);
  const [text, setText] = useState("");
  const [tags, setTags] = useState("");
  const [showCrisis, setShowCrisis] = useState(false);
  const crisis = useCrisis(text);

  function post() {
    if (!text.trim()) return;
    if (crisis) setShowCrisis(true);
    const item = {
      id: crypto.randomUUID(),
      text: text.trim(),
      tags: tags
        .split(",")
        .map(t => t.trim())
        .filter(Boolean),
      time: new Date().toISOString(),
      likes: 0,
      replies: [],
    };
    setFeed([item, ...feed]);
    setText("");
    setTags("");
  }

  function like(id) {
    setFeed(feed.map(x => (x.id === id ? { ...x, likes: x.likes + 1 } : x)));
  }

  function reply(id, content) {
    setFeed(
      feed.map(x =>
        x.id === id ? { ...x, replies: [...x.replies, { id: crypto.randomUUID(), content, time: new Date().toISOString() }] } : x
      )
    );
  }

  const [query, setQuery] = useState("");
  const filtered = useMemo(() => {
    const q = query.toLowerCase();
    return feed.filter(x =>
      !q ? true : x.text.toLowerCase().includes(q) || x.tags.some(t => t.toLowerCase().includes(q))
    );
  }, [feed, query]);

  return (
    <div className="grid gap-5">
      <CrisisModal open={showCrisis} onClose={() => setShowCrisis(false)} />
      <Section
        title="Chia sáº» áº©n danh"
        desc="Viáº¿t Ä‘iá»u báº¡n muá»‘n nÃ³i. TÃ´n trá»ng â€“ KhÃ´ng phÃ¡n xÃ©t."
        right={<Pill>áº¨n danh</Pill>}
      >
        <Textarea value={text} onChange={setText} placeholder="MÃ¬nh Ä‘ang tháº¥yâ€¦" />
        <div className="mt-2 grid sm:grid-cols-3 gap-2">
          <Input value={tags} onChange={setTags} placeholder="Hashtag, cÃ¡ch nhau báº±ng dáº¥u pháº©y (vd: #cÄƒng_tháº³ng_thi_cá»­, #Ä‘am_mÃª_láº­p_trÃ¬nh)" />
          <Input value={query} onChange={setQuery} placeholder="TÃ¬m theo tá»« khoÃ¡/hashtag" />
          <Button onClick={post}>ÄÄƒng</Button>
        </div>
        <p className="text-xs text-slate-500 mt-2">LÆ°u Ã½: Ná»™i dung nháº¡y cáº£m sáº½ hiá»‡n cáº£nh bÃ¡o há»— trá»£.</p>
      </Section>

      <Section title="DÃ²ng cháº£y tÃ¢m sá»±" desc="BÃ i viáº¿t má»›i nháº¥t á»Ÿ trÃªn cÃ¹ng.">
        <div className="grid gap-4">
          {filtered.length === 0 && <p className="text-slate-500 text-sm">ChÆ°a cÃ³ bÃ i viáº¿t phÃ¹ há»£p.</p>}
          {filtered.map(item => (
            <article key={item.id} className="border border-slate-200 rounded-2xl p-4">
              <div className="flex justify-between items-start">
                <div className="flex items-center gap-3">
                  <img alt="avatar" src="https://i.pravatar.cc/40?img=12" className="w-9 h-9 rounded-full" />
                  <div>
                    <div className="font-medium text-slate-800">Báº¡n áº©n danh</div>
                    <div className="text-xs text-slate-500">{new Date(item.time).toLocaleString()}</div>
                  </div>
