];

function Tests() {
  const [ans, setAns] = useState({});
  const [result, setResult] = useState(null);

  function submit() {
    const score = { R:0, I:0, A:0, S:0, E:0, C:0 };
    Object.entries(ans).forEach(([i, v]) => { if (v) score[RIASEC_QUESTIONS[i].k] += 1; });
    const sorted = Object.entries(score).sort((a,b) => b[1]-a[1]).map(([k]) => k).slice(0,3).join("");
    setResult({ code: sorted, score });
  }

  return (
    <div className="grid gap-5">
      <Section title="Tráº¯c nghiá»‡m tÃ­nh cÃ¡ch â€“ nghá» (mini)" desc="PhiÃªn báº£n rÃºt gá»n RIASEC (chá»‰ tham kháº£o).">
        <div className="grid gap-3">
          {RIASEC_QUESTIONS.map((it, i) => (
            <label key={i} className="flex items-center gap-3 p-3 rounded-xl border">
              <input type="checkbox" checked={!!ans[i]} onChange={e => setAns({ ...ans, [i]: e.target.checked })} />
              <span>{it.q}</span>
            </label>
          ))}
        </div>
        <Button onClick={submit} className="mt-3">Xem gá»£i Ã½</Button>
        {result && (
          <div className="mt-4 p-4 rounded-2xl bg-slate-50">
            <div className="font-semibold">MÃ£ ná»•i trá»™i: {result.code}</div>
            <p className="text-sm text-slate-600 mt-1">
              Gá»£i Ã½ (tham kháº£o):
              {" "}
              {result.code.includes("I") && "Khoa há»c dá»¯ liá»‡u, CNTT, nghiÃªn cá»©u."}
              {result.code.includes("A") && " Thiáº¿t káº¿ Ä‘á»“ hoáº¡, truyá»n thÃ´ng, UI/UX."}
              {result.code.includes("S") && " TÃ¢m lÃ½ há»c Ä‘Æ°á»ng, giÃ¡o dá»¥c, y táº¿ cá»™ng Ä‘á»“ng."}
              {result.code.includes("E") && " Quáº£n trá»‹ kinh doanh, khá»Ÿi nghiá»‡p, marketing."}
              {result.code.includes("R") && " Ká»¹ thuáº­t, cÆ¡ khÃ­, Ä‘iá»‡n tá»­, kiáº¿n trÃºc."}
