import React, { useEffect, useMemo, useState } from "react";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import AuthProvider, { useAuth } from "./auth/AuthContext";
import ProtectedRoute from "./auth/ProtectedRoute";
import Login from "./pages/Login";
import Signup from "./pages/Signup";
// import cÃ¡c trang hiá»‡n cÃ³ cá»§a báº¡n: Home, Journal, Forum, ...
/**
 * SafeSpace â€” MVP singleâ€‘file React app (VN)
 * Notes:
 * - No backend. All data lives in localStorage => demo/prototype only.
 * - Includes: áº¨n danh tÃ¢m sá»±, Nháº­t kÃ½, Báº£ng tÃ¢m tráº¡ng, Diá»…n Ä‘Ã n nghá» nghiá»‡p,
 *   Tráº¯c nghiá»‡m Ä‘á»‹nh hÆ°á»›ng (RIASEC mini), Há»“ sÆ¡/Portfolio, Danh báº¡ cá»‘ váº¥n,
 *   NhÃ³m tháº£o luáº­n, Sá»± kiá»‡n trá»±c tuyáº¿n, Gamification, ThÃ´ng bÃ¡o/Nháº¯c nhá»Ÿ,
 *   Bá»™ lá»c an toÃ n (phÃ¡t hiá»‡n tá»« khoÃ¡ tiÃªu cá»±c) & quy táº¯c cá»™ng Ä‘á»“ng.
 * - Design: Tailwind utility classes (no import required in Canvas).
 */

// ---------- Utilities ----------
const KEY = {
  diary: "ss_diary",
  feed: "ss_feed",
  mood: "ss_mood",
  forum: "ss_forum",
  profile: "ss_profile",
  groups: "ss_groups",
  events: "ss_events",
  badges: "ss_badges",
  reminders: "ss_reminders",
};

const defaultProfile = {
  name: "Há»c sinh áº©n danh",
  grade: "Lá»›p 12",
  interests: ["ToÃ¡n", "Láº­p trÃ¬nh"],
  bio: "MÃ¬nh thÃ­ch chia sáº» tips há»c vÃ  dá»± Ã¡n nhá».",
  projects: [
    { title: "Web flashcard HÃ³a 11", link: "", desc: "Ã”n táº­p pháº£n á»©ng" },
  ],
  achievements: ["Giáº£i NhÃ¬ HSG Tin tá»‰nh"],
  avatar: "https://avatars.githubusercontent.com/u/9919?s=200&v=4",
};

const crisisKeywords = [
  "tá»± tá»­", "tá»± lÃ m Ä‘au", "chÃ¡n sá»‘ng", "tráº§m cáº£m náº·ng", "há»§y hoáº¡i báº£n thÃ¢n",
  "suicide", "kill myself", "end my life",
];

function useLocal(key, init) {
  const [state, set] = useState(() => {
    try {
      const s = localStorage.getItem(key);
      return s ? JSON.parse(s) : init;
    } catch {
      return init;
    }
  });
  useEffect(() => {
    try {
      localStorage.setItem(key, JSON.stringify(state));
    } catch {}
  }, [key, state]);
  return [state, set];
}

function Home() {
  return (
    <div className="p-6">
      <h1 className="text-xl font-semibold">SafeSpace</h1>
      <p className="mt-6 text-slate-600">Khu vá»±c an toÃ n Ä‘á»ƒ chia sáº» cáº£m xÃºcâ€¦</p>
    </div>
  );
}

function Section({ title, desc, right, children }) {
  return (
    <div className="bg-white rounded-2xl shadow p-5 border border-slate-100">
      <div className="flex items-start justify-between gap-4 mb-4">
        <div>
          <h2 className="text-xl font-semibold text-slate-800">{title}</h2>
          {desc && <p className="text-slate-500 text-sm mt-1">{desc}</p>}
        </div>
        <div className="shrink-0">{right}</div>
      </div>
      {children}
    </div>
  );
}

function Pill({ children }) {
  return (
    <span className="px-2.5 py-1 rounded-full text-xs bg-slate-100 text-slate-700">
      {children}
    </span>
  );
}


function Textarea({ value, onChange, placeholder }) {
  return (
    <textarea
      className="w-full rounded-xl border border-slate-200 focus:ring-2 focus:ring-sky-300 focus:border-sky-300 p-3 outline-none"
      rows={4}
      value={value}
      onChange={(e) => onChange(e.target.value)}
      placeholder={placeholder}
    />
  );
}

function Input({ value, onChange, placeholder, type = "text" }) {
  return (
    <input
      type={type}
      value={value}
      onChange={(e) => onChange(e.target.value)}
      placeholder={placeholder}
      className="w-full rounded-xl border border-slate-200 focus:ring-2 focus:ring-sky-300 focus:border-sky-300 p-3 outline-none"
    />
  );
}

function Button({ children, className = "", onClick, type = "button" }) {
  return (
    <button
      type={type}
      onClick={onClick}
      className={`rounded-xl px-4 py-2 bg-sky-600 hover:bg-sky-700 text-white shadow ${className}`}
    >
      {children}
    </button>
  );
}

// ---------- Tabs (Ä‘á»•i tÃªn theo phong cÃ¡ch trÆ°á»ng há»c) ----------
const tabs = [
  { id: "feed", label: "Confession TrÆ°á»ng Tui" },
  { id: "diary", label: "KÃ½ Sá»± 'Äi Há»c'" },
  { id: "mood", label: "Check 'Mood' HÃ´m Nay" },
  { id: "career", label: "GÃ³c Äá»‹nh HÆ°á»›ng 'Gen Z'" },
  { id: "tests", label: "Quiz 'Hiá»ƒu MÃ¬nh, Chá»n TrÆ°á»ng'" },
  { id: "profile", label: "Há»c Báº¡ 4.0" },
  { id: "mentors", label: "List Tháº§y CÃ´ 'Gá»¡ Rá»‘i'" },
  { id: "groups", label: "Team 'CÃ¹ng Tiáº¿n'" },
  { id: "events", label: "Event 'Hot' TrÆ°á»ng MÃ¬nh" },
  { id: "rewards", label: "SÆ°u Táº§m 'ThÃ nh TÃ­ch'" },
  { id: "reminders", label: "BÃ¡o Deadline / Nháº¯c Lá»‹ch Thi" },
  { id: "rules", label: "Ná»™i Quy 'VÄƒn Minh'" },
];

// ---------- Safety Banner ----------
function SafetyBanner() {
  return (
    <div className="rounded-2xl p-4 bg-gradient-to-r from-sky-50 to-emerald-50 border border-slate-200">
      <p className="text-sm text-slate-700">
        ðŸŒ¿ <strong>GÃ³c An YÃªn TrÆ°á»ng MÃ¬nh:</strong> NÆ¡i há»c trÃ² sáº» chia cáº£m xÃºc,
        há»c há»i vÃ  cÃ¹ng tiáº¿n bá»™. TÃ´n trá»ng â€“ KhÃ´ng phÃ¡n xÃ©t â€“ áº¨n danh tÃ¹y chá»n.
      </p>
    </div>
  );
}

// ---------- Crisis Detector ----------
function useCrisis(text) {
  return useMemo(() => {
    if (!text) return null;
    const t = text.toLowerCase();
    const hit = crisisKeywords.find(k => t.includes(k));
    return hit || null;
  }, [text]);
}

function CrisisModal({ open, onClose }) {
  if (!open) return null;
  return (
    <div className="fixed inset-0 z-50 grid place-items-center bg-black/40 p-4">
      <div className="bg-white rounded-2xl max-w-lg w-full p-6 shadow-xl">
        <h3 className="text-lg font-semibold text-rose-700">Dáº¥u hiá»‡u khá»§ng hoáº£ng Ä‘Æ°á»£c phÃ¡t hiá»‡n</h3>
        <p className="text-slate-700 mt-2 text-sm leading-relaxed">
          MÃ¬nh ráº¥t tiáº¿c vÃ¬ báº¡n Ä‘ang tráº£i qua cáº£m xÃºc náº·ng ná». HÃ£y nÃ³i chuyá»‡n vá»›i ngÆ°á»i tin cáº­y
          (gia Ä‘Ã¬nh, giÃ¡o viÃªn, báº¡n thÃ¢n). Náº¿u tháº¥y nguy cáº¥p, gá»i <strong>115</strong> hoáº·c liÃªn há»‡ trung tÃ¢m tÆ° váº¥n tÃ¢m lÃ½ táº¡i Ä‘á»‹a phÆ°Æ¡ng.
        </p>
        <ul className="mt-3 text-sm text-slate-700 list-disc pl-5">
          <li>GiÃ¡o viÃªn chá»§ nhiá»‡m / Tá»• tÆ° váº¥n tÃ¢m lÃ½ trÆ°á»ng.</li>
          <li>ÄÆ°á»ng dÃ¢y nÃ³ng Bá»‡nh viá»‡n TÃ¢m tháº§n Ä‘á»‹a phÆ°Æ¡ng.</li>
          <li>Báº¡n cÃ³ thá»ƒ chuyá»ƒn bÃ i viáº¿t sang <em>riÃªng tÆ°</em> trong Nháº­t kÃ½.</li>
        </ul>
        <div className="mt-4 flex justify-end gap-2">
          <button onClick={onClose} className="px-4 py-2 rounded-xl bg-slate-100">ÄÃ£ hiá»ƒu</button>
        </div>
      </div>
    </div>
  );
}

// ---------- Trang Confession ----------
function Feed() {
  const [feed, setFeed] = useLocal(KEY.feed, []);
  const [text, setText] = useState("");

  function post() {
    if (!text.trim()) return;
    const item = {
      id: crypto.randomUUID(),
      text: text.trim(),
      time: new Date().toISOString(),
      likes: 0,
    };
    setFeed([item, ...feed]);
    setText("");
  }

  return (
    <Section
      title="Confession TrÆ°á»ng Tui"
      desc="Chia sáº» tÃ¢m sá»± áº©n danh â€“ NÆ¡i há»c trÃ² nÃ³i tháº­t lÃ²ng mÃ¬nh."
    >
      <Textarea
        value={text}
        onChange={setText}
        placeholder="Tui muá»‘n nÃ³i lÃ ..."
      />
      <div className="mt-3 flex gap-2">
        <Button onClick={post}>ÄÄƒng Confession</Button>
      </div>
      <div className="mt-4 grid gap-3">
        {feed.map((f) => (
          <div key={f.id} className="border p-3 rounded-xl">
            <p className="text-slate-800 whitespace-pre-wrap">{f.text}</p>
            <div className="text-xs text-slate-500 mt-1">
              {new Date(f.time).toLocaleString()}
            </div>
          </div>
        ))}
      </div>
    </Section>
  );
}

// ---------- Nháº­t kÃ½ há»c táº­p ----------
function Diary() {
  const [entries, setEntries] = useLocal(KEY.diary, []);
  const [text, setText] = useState("");
  function addEntry() {
    if (!text.trim()) return;
    setEntries([{ id: crypto.randomUUID(), text, time: new Date() }, ...entries]);
    setText("");
  }
  return (
    <Section title="KÃ½ Sá»± 'Äi Há»c'" desc="Viáº¿t láº¡i hÃ nh trÃ¬nh há»c táº­p má»—i ngÃ y.">
      <Textarea
        value={text}
        onChange={setText}
        placeholder="HÃ´m nay á»Ÿ trÆ°á»ng..."
      />
      <Button onClick={addEntry}>LÆ°u nháº­t kÃ½</Button>
      <div className="mt-3">
        {entries.map((e) => (
          <div key={e.id} className="border p-3 rounded-xl mb-2">
            <div className="text-xs text-slate-500">
              {new Date(e.time).toLocaleString()}
            </div>
            <p className="mt-1 text-slate-800">{e.text}</p>
          </div>
        ))}
      </div>
    </Section>
  );
}

// ---------- Mood Board ----------
function MoodBoard() {
  const [moods, setMoods] = useLocal(KEY.mood, []);
  const palette = ["ðŸ˜„","ðŸ™‚","ðŸ˜","ðŸ˜•","ðŸ˜¢","ðŸ˜¤","ðŸ˜´"];
  function add(m) { setMoods([{ id: crypto.randomUUID(), m, t: new Date().toISOString() }, ...moods]); }
  return (
    <div className="grid gap-5">
      <Section title="Báº£ng tÃ¢m tráº¡ng" desc="Theo dÃµi sá»± thay Ä‘á»•i theo thá»i gian.">
        <div className="flex flex-wrap gap-2">
          {palette.map(x => <button key={x} onClick={() => add(x)} className="text-2xl hover:scale-110 transition">{x}</button>)}
        </div>
        <div className="mt-4 grid gap-2">
          {moods.length === 0 && <p className="text-sm text-slate-500">ChÆ°a ghi nháº­n tÃ¢m tráº¡ng.</p>}
          {moods.map(x => (
            <div key={x.id} className="flex items-center gap-2 text-slate-700">
              <span className="text-xl">{x.m}</span>
              <span className="text-xs">{new Date(x.t).toLocaleString()}</span>
            </div>
          ))}
        </div>
      </Section>
    </div>
  );
}

// ---------- Career Forum ----------
function CareerForum() {
  const [topics, setTopics] = useLocal(KEY.forum, [
    { id: crypto.randomUUID(), title: "Há»c ngÃ nh CNTT cáº§n nhá»¯ng gÃ¬?", author: "Cá»±u há»c sinh", body: "Lá»™ trÃ¬nh, dá»± Ã¡n, thá»±c táº­pâ€¦", replies: [] },
  ]);
  const [t, setT] = useState("");
  const [b, setB] = useState("");

  function add() {
    if (!t.trim() || !b.trim()) return;
    setTopics([{ id: crypto.randomUUID(), title: t.trim(), author: "Báº¡n áº©n danh", body: b.trim(), replies: [] }, ...topics]);
    setT(""); setB("");
